import React, { useState } from 'react';
import Typical from 'react-typical';
import './Contact.css';
import imgBack from '../../assets/Contact/mailz.jpeg';
import load1 from '../../assets/Contact/load2.gif';
import ScreenHeading from '../../utilities/ScreenHeading/ScreenHeading'
import ScrollService from '../../utilities/ScrollService'
import Animations from '../../utilities/Animations';

export default function Contact(props) {
    let fadeInScreenHandler = (screen) => {
        if (screen.fadeScreen !== props.id) return;

        Animations.animations.fadeInScreen(props.id);
      };

      const fadeInSubscription =
        ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler);

    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [message, setMessage] = useState("");
    const [banner, setBanner] = useState("");
    const [bool, setBool] = useState(false);

    const handleName = (e) => {
        setName(e.target.value);
    };
    const handleEmail = (e) => {
        setEmail(e.target.value);
    };
    const handleMessage = (e) => {
        setMessage(e.target.value);
    };

    const submitForm = (e) => {
        e.preventDefault();
    }

  return (
    <div className='main-container' id={props.id || ''}>
        <ScreenHeading subHeading = {"You have something for me? ☺️"} title = {"Contact Me"}/>
        <div className='central-form'>
            <div className='col'>
            <h2 className='title'>
                <Typical
                  loop={Infinity}
                  steps={[
                    "Send me something ☺️", 
                    1000,

                  ]}
                />
              </h2>
              <a href="https://www.instagram.com/marlinism_/">
                <i className="fa fa-instagram"></i>
              </a>
              <a href="https://www.linkedin.com/in/tuong-banh">
                <i className="fa fa-linkedin-square"></i>
              </a>
              <a href="https://github.com/marlinism">
                <i className="fa fa-github-square"></i>
              </a>
            </div>
            <div className='back-form'>
                <div className='img-back'>
                    <h4>Send Your Message Here!</h4>
                    <img src={imgBack} alt='Image not found!'/>
                </div>
                <form onSubmit={submitForm}>
                    <p>{banner}</p>
                    <label htmlFor='name'>Name</label>
                    <input type='text'
                    onChange={handleName}
                    value={name}
                    />

                    <label htmlFor='email'>Email</label>
                    <input type='email'
                    onChange={handleEmail}
                    value={email}
                    />

                    <label htmlFor='message'>Message</label>
                    <textarea type='text'
                    onChange={handleMessage}
                    value={message}
                    />

                    <div className='send-btn'>
                        <button type='submit'>Send 
                        <i className='fa fa-paper-plane'/>
                        </button>
                    </div>
                </form>
            </div>
        </div>
    </div>
  );
}
